mapper.select:
SELECT COUNT(1) FROM MOBILE_ONETOONE_ASK A WHERE #{idnos} LIKE CONCAT('%', IDNO, '%') AND ASK_TYPE = #{ask_type}
AND TITLE LIKE CONCAT('%', #{search_keyword}, '%')
AND CONTENTS LIKE CONCAT('%', #{search_keyword}, '%')

SELECT * FROM ( SELECT (ROW_NUMBER() OVER()) AS RN, TB.* FROM (
SELECT SEQ , IDNO , ASK_TYPE , TITLE , PHONE , EMAIL , ANSWER_ID , ANSWER_DATE , STAT , INID REG_ID , UPID MOD_ID , INIP , DATE_FORMAT(INDT, '%Y%m%d%H%i%s') REG_DT , DATE_FORMAT(UPDT, '%Y%m%d%H%i%s') MOD_DT FROM MOBILE_ONETOONE_ASK A WHERE #{idnos} LIKE CONCAT('%', IDNO, '%') AND ASK_TYPE = #{ask_type}
AND TITLE LIKE CONCAT('%', #{search_keyword}, '%')
AND CONTENTS LIKE CONCAT('%', #{search_keyword}, '%')
ORDER BY SEQ DESC
) TB ) TBS LIMIT #{record_count_per_page} OFFSET #{first_index}

mapper.insert:

	SELECT IFNULL(MAX(SEQ), 0) + 1 FROM MOBILE_ONETOONE_ASK

INSERT INTO MOBILE_ONETOONE_ASK ( 
	SEQ
	, IDNO
	, ASK_TYPE
	, TITLE
	, CONTENTS
	, PHONE
	, EMAIL
	, ANSWER_ID
	, ANSWER_CONTENTS
	, ANSWER_DATE
	, STAT
	, INID
	, UPID
	, INIP
	, INDT
	, UPDT
) VALUES (
	#{seq}
	, #{idno}
	, #{ask_type}
	, #{title}
	, #{contents}
	, #{phone}
	, #{email}
	, NULL
	, NULL
	, NULL
	, #{stat}
	, #{reg_id}
	, #{mod_id}
	, #{user_ip}
	, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
)

mapper.update:
UPDATE
	MOBILE_ONETOONE_ASK
SET
	UPID = #{mod_id}
	, UPDT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	, INIP = #{user_ip}

		, TITLE = #{title}


		, CONTENTS = #{contents}


		, PHONE = #{phone}


		, EMAIL = #{email}

WHERE
	SEQ = #{seq}
