mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM (
SELECT SEQ , IDNO , ASK_TYPE , TITLE , PHONE , EMAIL , ANSWER_ID , ANSWER_DATE , STAT , INID REG_ID , UPID MOD_ID , INIP , TO_CHAR(INDT, 'YYYYMMDDHH24MISS') REG_DT , TO_CHAR(UPDT, 'YYYYMMDDHH24MISS') MOD_DT FROM KU_USER01.MOBILE_ONETOONE_ASK A WHERE #{idnos} LIKE '%' || IDNO || '%'
AND ASK_TYPE = #{search_ask_type}
AND TITLE LIKE '%' || #{search_keyword} || '%'
AND CONTENTS LIKE '%' || #{search_keyword} || '%'
ORDER BY SEQ DESC
) TB ) WHERE rn BETWEEN #{first_index} + 1 AND #{first_index} + #{record_count_per_page}

mapper.insert:

	SELECT KU_USER01.MOBILE_ONETOONE_ASK_SEQ.NEXTVAL FROM DUAL

INSERT INTO KU_USER01.MOBILE_ONETOONE_ASK ( 
	SEQ
	, IDNO
	, ASK_TYPE
	, TITLE
	, CONTENTS
	, PHONE
	, EMAIL
	, ANSWER_ID
	, ANSWER_CONTENTS
	, ANSWER_DATE
	, STAT
	, INID
	, UPID
	, INIP
	, INDT
	, UPDT
) VALUES (
	#{seq}
	, #{idno}
	, #{ask_type}
	, #{title}
	, #{contents}
	, #{phone}
	, #{email}
	, NULL
	, NULL
	, NULL
	, '0'
	, #{reg_id}
	, #{mod_id}
	, #{user_ip}
	, SYSDATE
	, SYSDATE
)

mapper.update:
UPDATE
	KU_USER01.MOBILE_ONETOONE_ASK
SET
	UPID = #{mod_id}
	, UPDT = SYSDATE
	, INIP = #{user_ip}

		, TITLE = #{title}


		, CONTENTS = #{contents}


		, PHONE = #{phone}


		, EMAIL = #{email}

WHERE
	SEQ = #{seq}
