mapper.select:
SELECT COUNT(1) FROM VIEW_MOBILECARD_CODE
AND UP_CD_SEQ = #{up_cd_seq}
GROUP BY COLUMNNAME

SELECT * FROM ( SELECT (ROW_NUMBER() OVER()) AS RN, TB.* FROM (
SELECT CODE CD_SEQ , CODENAME CD_NM , CODE CD_ID , COLUMNNAME UP_CD_SEQ FROM VIEW_MOBILECARD_CODE
AND UP_CD_SEQ = #{up_cd_seq}
GROUP BY COLUMNNAME
) TB ) TBS LIMIT #{record_count_per_page} OFFSET #{first_index}
